import React from 'react';
import AppHeader from "../app-header";
import TodoList from "../todo-list";
import SearchPanel from "../search-panel";
import Footer from "../footer";
import ItemStatusFilter from "../item-status-filter";

import './app.css';

const App = () => {

    const todoData = [
        {label: 'Кровать', important: false, id: 1},
        {label: 'Трубы в ванную и кухню', important: true, id: 2},
        {label: 'Туалет', important: false, id: 3},
        {label: 'Кран с душем в ванную', important: false, id: 4}
    ]

    return (
        <div className="todo-app">
            <AppHeader toDo={1} done={3}/>
            <div className="top-panel d-flex">
                <SearchPanel />
                <ItemStatusFilter />
            </div>

            <TodoList todos={todoData}
                      onDel={(id) => console.log(id)}
                      //когда мы кликаем на кнопку(событие кнопки) в List-Item, мы вызываем функцию которую нам передал Todo-List, Todo-List в свою очередь вызывает функцию. которую передал App, таким образом App узнает
            />
            <Footer />
        </div>
    );
};
export default App;
