import React from "react";
import "./list-item.css";

//Компоненты-Классы с внутренним состоянием.

class ListItem extends React.Component {

    render() {

        const { label, important = false}      = this.props
        const lispanStyle = {
            color: important ? '#bf3f3f' : 'black',
            fontWeightw: important ? 'bols' : 'normal',
        }
        return <span>

        <span
            className="todo-list-item-label"
            style={lispanStyle}>
            {label}
        </span>

        <button type="button"
                className="btn btn-outline-success btn-sm float-right">
            <i className="fa fa-exclamation-circle"/>
        </button>
        <button type="button"
                className="btn btn-outline-danger btn-sm float-right">
            <i className="fa fa-trash-o"/>
        </button>


    </span>
    }
}

//у Функций нет внутреннего состояния, нам некуда сохранить свойства компонента которые могут изменяться во время работы программы, к примеру счестчик.
const ListItemFunc = ( { label, important = false} ) => {

    const lispanStyle = {
        color: important ? '#bf3f3f' : 'black',
        fontWeightw: important ? 'bols' : 'normal',
    }
    return <span>

        <span
            className="todo-list-item-label"
            style={lispanStyle}>
            {label}
        </span>

        <button type="button"
                className="btn btn-outline-success btn-sm float-right">
            <i className="fa fa-exclamation-circle" />
        </button>
        <button type="button"
                className="btn btn-outline-danger btn-sm float-right">
            <i className="fa fa-trash-o" />
        </button>


    </span>
}
export default ListItem;
