import React, {Component} from "react";
import "./list-item.css";

//Компоненты-Классы с внутренним состоянием.

export default class ListItem extends Component {
    //1 метод
    //функция на прототипе работает только с bind
    // onLabelClick() {
    //     console.log(`Done: ${this.props.label}`)
    // }

    //2 метод консервативный
    // constructor() {
    //     super();
    //     /*создадим функцую не на прототипе, а на самом объекте и объявим как функцию стрелку чтобы this сохранялся правильно*/
    //     this.onLabelClick = () => {
    //         console.log(`Done: ${this.props.label}`)
    //     }
    // }

    //3 метод
    //поле-класса в пропосиле 3 есть class-fields равносильно тому что мы сделали в конструкторе, там мы объявляли функцию в конструкторе
    onLabelClick = () => {
        console.log(`Done: ${this.props.label}`)
        this.setState({
            done: true
        })
    }
    //когда мы добавили новые элементы к текущему State мы не обновляли предидущие вызовы setState, новый объект наложится поверх существующего состояния, реакт сам позаботится о том чтобы объеденить текующие значения  сновыми
    onButtonImportant = () => {
        this.setState((state) => {
            return {

            }
        })
        // вместо нового состояния мы в этот сетСтейт передаем еще одну функцию, которая в свою очередь принимает наш текущий стейт   -  этот сетСтейт нужно выполнить только тогда, когда наш текущий стейт уже будет в его финальном состояние
        console.log(`Done: ${this.props.important}`)
    }
    //мы кликаем на элемент label срабатывает функция onLabelClick - она выполняет setState, setState говорит состояние компонента именилась и React заново вызывает функцию render, функция рендер возвраает новую структуру для нашего компонента, Реакт запускает реконсилейшен алгоритм и находит что изменился только класс, и обновляет егов DOM дереве

    state = {
        done: false,
        important: false
    }
    //после того как мы впервый раз проинициализировали state его больше нельзя изменять напрямую. его можно читать как в функции рендер, для того чтобы его изменять нужна функция this.setState

    render() {

        const { label} = this.props

        const { done, important } = this.state
        let classNames = 'todo-list-item'
        if (done) {
            classNames += ' done'
        }
        if (important) {
            classNames += ' important'
        }

        // const lispanStyle = {
        //     color: important ? '#d43939' : 'black',
        //     fontWeight: important ? 'bols' : 'normal',
        // }
        return <span className={classNames}>
        <span
            className="todo-list-item-label"
            // style={lispanStyle}
            onClick={ this.onLabelClick }>
            {/*onClick={ this.onLabelClick.bind(this) }> не красиво так привязывать намертво к this - создается новая функция которую оборачивает bind*/}
            {/*мы передаем функцию а не вызываем, без (), иначе выведет undefande*/}
            {label}
        </span>

        <button type="button"
                className="btn btn-outline-success btn-sm float-right"
                onClick={ this.onButtonImportant }>
            <i className="fa fa-exclamation"/>
        </button>
        <button type="button"
                className="btn btn-outline-danger btn-sm float-right">
            <i className="fa fa-trash-o"/>
        </button>


    </span>
    }
}

//у Функций нет внутреннего состояния, нам некуда сохранить свойства компонента которые могут изменяться во время работы программы, к примеру счестчик.
