import React from 'react';
import ReactDOM from 'react-dom';

const TodoList = () => {

    const items = ['покушать','выспаться'];
    return (
        <ul>
            <li>{items[0] }</li>
            <li>{items[1] }</li>
        </ul>
    );
}

const AppHeader = () => {
    return <h1>It's my Todo List</h1>
}

const SearchPanel = () => {

    const searchText = 'Type here to search'
    const searchStyle = {
        fontSize: '20px'
    }
    return <input
        placeholder={searchText}
        className="foo" //запомни аналог class в html
        htmlFor=""      //запомни аналог for в html
        style={searchStyle} //редко будешь использовать, обычно через className и класса в css
        //disabled
    />
}

const App = () => {

    const value = '<script>alert("инжекция не работает в JSX")</script>'

    const isLogged = true
    const loginBox = <div>Log in please</div>
    const welcomeBox = <span>Welcome back</span>

    return (
        <div>
            {value } //строка безопасно отрисовалась
            { isLogged ? welcomeBox : loginBox }
            <AppHeader />
            <SearchPanel />
            <TodoList />
            <Footer />
            <span>{(new Date()).toString()}</span>
        </div>
    );
}
const Footer = () => React.createElement('footer', null, '© Copyright Matthew Farrel Corporation')

console.log(<TodoList />);
console.log(<App />);
ReactDOM.render(<App />, document.getElementById('root'));
